part of 'home_bloc.dart';enum HomeStatus { light, night }class HomeState extends Equatable {  final HomeStatus status;  final List<Map> history;  const HomeState({ this.history = const [],this      .status = HomeStatus.light});  HomeState copyWith({int? index, int? page, List<Map>? history, HomeStatus? status}) {    return HomeState(         history: history ?? this.history,        status: status ?? this.status);  }  @override  List<Object?> get props => [history,  status,];}