part of 'home_bloc.dart';enum HomeStatus { light, night }class HomeState extends Equatable {  final int index;  final HomeStatus status;  final List<Map> history;  const HomeState({this.index = 1, this.history = const [],this      .status = HomeStatus.light});  HomeState copyWith({int? index, int? page, List<Map>? history, HomeStatus? status}) {    return HomeState(       index: index ?? this.index,        history: history ?? this.history,        status: status ?? this.status);  }  @override  List<Object?> get props => [index, history,  status,];}