import 'dart:io';import 'package:hive_flutter/hive_flutter.dart';class Repository {  static const String key = 'history';  static init() async {    await Hive.initFlutter();    var s = await Hive.openBox(key);  }  add(var value) async {    var box = Hive.box(key);    DateTime time = DateTime.now();    String fix = '${time.year}-${time.month}-${time.day} ${time.hour}:${time.minute}:${time.second}';    await box.add({value.toString(): fix});    print({value.toString(): fix});  }  readIndex() {    var box = Hive.box(key);    if(box.values.isEmpty){      return '1';    }    return  Map.from(box.values.last).keys.first;  }  getAll() {    var box = Hive.box(key);    var s = box.get(key);    return box.values.map((e) => Map.from(e)).toList();  }}