import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:tasksobes/bloc/home_bloc.dart';class DrawerCustom extends StatelessWidget {  const DrawerCustom({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Drawer(      child: SingleChildScrollView(        child: Padding(          padding: EdgeInsets.symmetric(horizontal: 10, vertical: MediaQuery.of(context).viewPadding.top),          child:  BlocBuilder<HomeBloc,HomeState>(              builder: (context,state){                final List<Map> history = state.history;                return Wrap(                  direction: Axis.vertical,                  children: List.generate(                      history.length,                          (index) => Row(                        children: [                          Text(                            history[index].keys == 1                                ? "Hello World"                                : "Bye "                                "World --- ${history[index].values.toString()}",                            maxLines: 1,                            overflow: TextOverflow.ellipsis,                          )                        ],                      )),                );              },            ),          ),        ),      );  }}